mapper.insert:
INSERT INTO users (
    username, password, nickname, email, phone, 
    avatar, gender, birthday, status, last_login_time,
    created_at, updated_at, is_deleted
) VALUES (
    #{username}, #{password}, #{nickname}, #{email}, #{phone},
    #{avatar}, #{gender}, #{birthday}, #{status}, #{lastLoginTime},
    #{createdAt}, #{updatedAt}, #{isDeleted}
)

mapper.update:
UPDATE users SET
    last_login_time = #{lastLoginTime}
WHERE id = #{id}
